import React, { Component } from "react";
import { Dialog, IconButton, Button, Icon, Grid } from "@material-ui/core";
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import { MuiPickersUtilsProvider, DateTimePicker } from "@material-ui/pickers";
import "date-fns";
import DateFnsUtils from "@date-io/date-fns";
import { addNewEvent, updateEvent, deleteEvent } from "./CalendarService";

class EventEditorDialog extends Component {
  state = {
    title: "",
    start: "",
    end: "",
    location: "",
    note: ""
  };

  handleChange = event => {
    event.persist();
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleFormSubmit = () => {
    let { id } = this.state;
    if (id) {
      updateEvent({
        ...this.state
      }).then(() => {
        this.props.handleClose();
      });
    } else {
      addNewEvent({
        id: this.generateRandomId(),
        ...this.state
      }).then(() => {
        this.props.handleClose();
      });
    }
  };

  handleDeleteEvent = () => {
    if (this.state.id) {
      deleteEvent(this.state).then(() => {
        this.props.handleClose();
      });
    }
  };

  handleDateChange = (date, name) => {
    this.setState({
      [name]: date
    });
  };

  generateRandomId = () => {
    let tempId = Math.random().toString();
    let id = tempId.substr(2, tempId.length - 1);
    return id;
  };

  componentWillMount() {
    this.setState({
      ...this.props.event
    });
  }

  render() {
    let { title, start, end, location, note } = this.state;
    let { open, handleClose } = this.props;

    return (
      <Dialog onClose={handleClose} open={open} maxWidth="xs" fullWidth={true}>
        <div className="flex flex-space-between flex-middle pl-16 pr-8 py-8 bg-primary">
          <h4 className="m-0 text-white">Add Events</h4>
          <IconButton onClick={handleClose}>
            <Icon className="text-white">clear</Icon>
          </IconButton>
        </div>

        <div className="p-16">
          <ValidatorForm ref="form" onSubmit={this.handleFormSubmit}>
            <TextValidator
              className="mb-24 w-100"
              label="Title"
              onChange={this.handleChange}
              type="text"
              name="title"
              value={title}
              validators={["required"]}
              errorMessages={["this field is required"]}
            />

            <Grid container spacing={4}>
              <Grid item sm={6} xs={12}>
                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                  <DateTimePicker
                    margin="none"
                    id="mui-pickers-date"
                    label="Start date"
                    inputVariant="standard"
                    type="text"
                    autoOk={true}
                    value={start}
                    onChange={date => this.handleDateChange(date, "start")}
                  />
                </MuiPickersUtilsProvider>
              </Grid>
              <Grid item sm={6} xs={12}>
                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                  <DateTimePicker
                    margin="none"
                    id="mui-pickers-date"
                    label="End date"
                    inputVariant="standard"
                    type="text"
                    autoOk={true}
                    value={end}
                    onChange={date => this.handleDateChange(date, "end")}
                  />
                </MuiPickersUtilsProvider>
              </Grid>
            </Grid>
            <div className="py-8" />
            <TextValidator
              className="mb-24 w-100"
              label="Location"
              onChange={this.handleChange}
              type="text"
              name="location"
              value={location}
              validators={["required"]}
              errorMessages={["this field is required"]}
            />

            <TextValidator
              className="mb-36 w-100"
              label="Note"
              onChange={this.handleChange}
              type="text"
              name="note"
              value={note}
              rowsMax={2}
              multiline={true}
              validators={["required"]}
              errorMessages={["this field is required"]}
            />

            <div className="flex flex-space-between flex-middle">
              <Button variant="contained" color="primary" type="submit">
                Save
              </Button>
              <Button onClick={this.handleDeleteEvent}>
                <Icon className="mr-8 text-middle">delete</Icon>
                Delete
              </Button>
            </div>
          </ValidatorForm>
        </div>
      </Dialog>
    );
  }
}

export default EventEditorDialog;
